public class Measurement extends Subsystem {

	private static final double calibrationTime = 4.0;
	private static final double GsToAccel = 386.0885826673; //Gs to in/s/s
	private static final double MMtoInches = 0.0393701; //Millimeters to inches
	private static final double RadsToDegrees = 180 / Math.PI;
	
    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTANTS

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTANTS

    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
    private final AnalogGyro robotGyro = RobotMap.measurementrobotGyro;
    private final PowerDistributionPanel robotPDB = RobotMap.measurementrobotPDB;
    private final AnalogInput robotUltrasonic = RobotMap.measurementrobotUltrasonic;

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
    private final MotorMonitor shooterMotorMonitor = new MotorMonitor(0, RobotMap.miniCIM);
    
    private final MPU_6050_I2C MPU_accelerometer = RobotMap.MPU_accelerometer;
    
    private Vector3d accelOffset = new Vector3d(),
    				 accelDrift = new Vector3d(),
    				 acceleration = new Vector3d(),
    				 distance = new Vector3d();
    
    private AverageFilter accelerationAverageX = new AverageFilter(9),
    					  accelerationAverageY = new AverageFilter(9),
    					  ultrasonicAverage = new AverageFilter(7);
    private FirstOrderLPF ultrasonicLPF = new FirstOrderLPF(0.2);
    
    private Timer time = new Timer();
    
    private double gyroDrift,
    			   robotAngle,
    			   startTime,
    			   gyroOffset,
    			   lastTime,
    			   shooterMotorSpeed;
    
    double testAngle;
    // Put methods for controlling this subsystem
    // here. Call these from Commands.
    

    public void initDefaultCommand() {
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DEFAULT_COMMAND

        setDefaultCommand(new Cmd_Default_Measure());

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DEFAULT_COMMAND

        // Set the default command for a subsystem here.
        // setDefaultCommand(new MySpecialCommand());
    }
    
    
    public void initialize() {
    	
    	time.start();
    	
    	startTime = getTime();
    	lastTime = getTime();
    	
    	
    	
    	
    	initAccelerometer();
    	
    	System.out.println("*****	Measurement Initialized");
    	
    	System.out.println("Gyro Drift Deg/s: " + gyroDrift);
    	
    }
    
    public void measure() {
    	
    	double timestamp = getTime(),
    			deltaT = timestamp - lastTime,
    			time = timestamp - startTime;
    	lastTime = timestamp;
    	
    	MPU_accelerometer.read();
    	
    	
    	/*	Accelerometer Filter	*/
    	double testAcceleration = MPU_accelerometer.getAccelX();
    	acceleration.X = accelerationAverageX.filter((testAcceleration));// - accelOffset.X);
    	//acceleration.Y = accelerationAverageY.filter((MPU_accelerometer.getAccelY()));// - accelOffset.Y);
    	
    	
    	/*	End Accelerometer Filter	*/
    	
    	/*	Gyro Measurements	*/
    	
    	robotAngle = MPU_accelerometer.getRobotAngle();// - (gyroDrift * (time)) - gyroOffset;
  	
    	/*	End Gyro Measurements	*/
    	
    	distance.X += (0.5) * acceleration.X * (deltaT * deltaT);
    	distance.Y += (0.5) * acceleration.Y * (deltaT * deltaT);
    	
    	/*	End Accelerometer Measurements	*/

    	/*	Ultrasonic Distance Calculations	*/
    	
    	ultrasonicLPF.filter(getUltrasonic());
    	
    	/*	End Ultrasonic Distance Calculations */
    	
    	/*	Test Prints	*/

    	System.out.println(getUltrasonic());
    	//System.out.println(MPU_accelerometer.rawAccelerometer.X);

    	/*	End Test Prints	*/
    	
    }
    
    public void findDrifts() {
  
       	/*	Gyro and Accelerometer Drift Calculations	*/
    	    	
    	double angle_start = MPU_accelerometer.getRobotAngle(),
    			accelX_start = MPU_accelerometer.getAccelX(),
    			accelY_start = MPU_accelerometer.getAccelY();
    	    	
    	Timer.delay(calibrationTime);
    	
    	double accelX_end = MPU_accelerometer.getAccelX(),
    			accelY_end = MPU_accelerometer.getAccelY(),
    			angle_end = MPU_accelerometer.getRobotAngle();
    	
    	accelDrift.X = (accelX_end - accelX_start) / calibrationTime;
    	accelDrift.Y = (accelY_end - accelY_start) / calibrationTime;
    	gyroDrift = (angle_end - angle_start) / calibrationTime;
    	
    	
    	/*	Calculate Accelerometer Offset	*/
    
    	
    	int iterations = 1000;
    	double startTime = getTime();
    	for(int i = 0; i <= iterations; i++) {
    		
    		gyroOffset += MPU_accelerometer.getRobotAngle() - (gyroDrift * (getTime() - startTime));
    		
    		accelOffset.X += MPU_accelerometer.getAccelX();
    		accelOffset.Y += MPU_accelerometer.getAccelY();
    		
    	}
    	
    	gyroOffset /= iterations;
    	accelOffset.X /= iterations;
    	accelOffset.Y /= iterations;
    }
    
    private void initAccelerometer() {
    	
    	//Reset the data vectors to zero
    	acceleration.reset();
    	distance.reset();
    	
    	//Reset the average filters
    	accelerationAverageX.reset();
    	accelerationAverageY.reset();
    	
    }
    
    
    public double getCurrent(int port) {
    	return robotPDB.getCurrent(port);
    }
    
    
    public double getShooterSpeed() { //Returns RPM of Motor
    	return shooterMotorSpeed;
    }

	public double getUltrasonicDistance() {
		return ultrasonicAverage.getAverage(); // in millimeters
	}
	
	public double getGyroAngle() {
		return robotAngle;
	}
	
	private double getTime() {
		return time.get() - startTime;
	}
	
	private double getUltrasonic() {
		
		double distance = (robotUltrasonic.getVoltage() * 5.) / .00488; //in millimeters
		
		return 1.044*(distance * MMtoInches)-0.8; //	These values are from hardware testing
		
	}
    
}

